// src/components/common/Card.jsx
import React from 'react';

/**
 * Card component for displaying content in a contained box
 */
const Card = ({
  title,
  bordered = true,
  className = '',
  children,
  footer,
  headerRight,
  ...rest
}) => {
  return (
    <div 
      className={`
        bg-gray-800 rounded-lg shadow-lg overflow-hidden
        ${bordered ? 'border border-gray-700' : ''}
        ${className}
      `}
      {...rest}
    >
      {title && (
        <div className="px-6 py-4 border-b border-gray-700 flex justify-between items-center">
          <h3 className="text-lg font-semibold text-white">{title}</h3>
          {headerRight && <div>{headerRight}</div>}
        </div>
      )}
      
      <div className="p-6">
        {children}
      </div>
      
      {footer && (
        <div className="px-6 py-3 bg-gray-750 border-t border-gray-700">
          {footer}
        </div>
      )}
    </div>
  );
};

// Define these before exporting them
Card.Header = ({ className = '', children, ...rest }) => (
  <div className={`px-6 py-4 border-b border-gray-700 ${className}`} {...rest}>
    {children}
  </div>
);

Card.Body = ({ className = '', children, ...rest }) => (
  <div className={`p-6 ${className}`} {...rest}>
    {children}
  </div>
);

Card.Footer = ({ className = '', children, ...rest }) => (
  <div className={`px-6 py-3 bg-gray-750 border-t border-gray-700 ${className}`} {...rest}>
    {children}
  </div>
);

// Also define these before exporting
const CardHeader = Card.Header;
const CardTitle = ({ children, ...props }) => <h3 className="text-lg font-semibold" {...props}>{children}</h3>;
const CardContent = Card.Body;
const CardDescription = ({ children, ...props }) => <p className="text-gray-400 text-sm" {...props}>{children}</p>;

export default Card;
export { CardHeader, CardTitle, CardContent, CardDescription };
