// WalletConnect.jsx - Wallet connection component

import React, { useState } from 'react';
import { useWallet } from '@aptos-labs/wallet-adapter-react';
import { useWalletContext } from '../../context/WalletContext';

const WalletConnect = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const { wallets } = useWallet();
  const { connectWallet } = useWalletContext();

  // Toggle dropdown
  const toggleDropdown = () => setIsDropdownOpen(!isDropdownOpen);

  // Handle wallet selection
  const handleWalletSelect = async (wallet) => {
    try {
      await connectWallet();
      setIsDropdownOpen(false);
    } catch (error) {
      console.error('Wallet connection failed:', error);
      // Error is handled in WalletContext
    }
  };

  return (
    <div className="relative inline-block text-left z-10">
      <button
        onClick={toggleDropdown}
        type="button"
        className="flex items-center space-x-2 px-4 py-2 bg-primary hover:bg-primary-dark text-white rounded-lg transition-colors duration-200"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-5 w-5"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z"
          />
        </svg>
        <span>Connect Wallet</span>
      </button>

      {isDropdownOpen && (
        <div className="origin-top-right absolute right-0 mt-2 w-64 rounded-md shadow-lg bg-white dark:bg-dark-lighter ring-1 ring-black ring-opacity-5 focus:outline-none">
          <div className="py-1">
            <div className="px-4 py-2 text-sm text-gray-700 dark:text-gray-200 border-b border-gray-200 dark:border-gray-700">
              Select a wallet
            </div>
            
            {wallets.length > 0 ? (
              wallets.map((wallet) => (
                <button
                  key={wallet.name}
                  onClick={() => handleWalletSelect(wallet)}
                  className="flex items-center w-full text-left px-4 py-3 text-sm text-gray-700 dark:text-gray-200 hover:bg-gray-100 dark:hover:bg-gray-700"
                >
                  <img
                    src={wallet.icon}
                    alt={`${wallet.name} icon`}
                    className="w-6 h-6 mr-2"
                    onError={(e) => {
                      // Fallback for missing icons
                      e.target.src = '/assets/images/wallet-generic.png';
                    }}
                  />
                  <div>
                    <div className="font-medium">{wallet.name}</div>
                    <div className="text-xs text-gray-500 dark:text-gray-400">
                      {wallet.installed ? 'Detected' : 'Not installed'}
                    </div>
                  </div>
                </button>
              ))
            ) : (
              <div className="px-4 py-3 text-sm text-gray-500 dark:text-gray-400">
                No compatible wallets found. Please install an Aptos wallet.
              </div>
            )}
            
            <div className="px-4 py-2 text-xs text-gray-500 dark:text-gray-400 border-t border-gray-200 dark:border-gray-700">
              <a
                href="https://aptos.dev/guides/wallet-adapter/"
                target="_blank"
                rel="noopener noreferrer"
                className="hover:text-primary dark:hover:text-primary-light"
              >
                Learn about Aptos wallets →
              </a>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default WalletConnect;
