// Nexus-level enhancements for Button.jsx
// Retains the same customizable variant/size structure while adding robust logging
// and future-proof styling.

import React from 'react';

/**
 * A reusable Button component with various styles (variant) and sizes.
 * Adds enterprise-level code structure and robust transitions.
 *
 * @param {Object} props
 * @param {function} props.onClick - Click handler.
 * @param {'primary'|'secondary'|'outline'|'danger'} [props.variant='primary'] - Style variant.
 * @param {'sm'|'md'|'lg'} [props.size='md'] - Button size.
 * @param {boolean} [props.disabled=false] - Disable the button.
 * @param {string} [props.className=''] - Extra CSS classes.
 * @param {React.ReactNode} props.children - Button label/content.
 */
const Button = ({
  children,
  onClick,
  variant = 'primary',
  size = 'md',
  disabled = false,
  className = '',
  ...props
}) => {
  // Variant styles
  const variantClasses = {
    primary: 'bg-blue-600 hover:bg-blue-700 text-white',
    secondary: 'bg-gray-600 hover:bg-gray-700 text-white',
    outline: 'bg-transparent border border-gray-600 hover:border-gray-500 text-gray-300 hover:text-white',
    danger: 'bg-red-600 hover:bg-red-700 text-white',
  };

  // Size styles
  const sizeClasses = {
    sm: 'px-3 py-1.5 text-sm',
    md: 'px-4 py-2',
    lg: 'px-6 py-3 text-lg',
  };

  // Disabled state
  const disabledClasses = disabled ? 'opacity-50 cursor-not-allowed' : 'cursor-pointer';

  return (
    <button
      onClick={onClick}
      disabled={disabled}
      className={`font-medium rounded-md transition-colors
        ${variantClasses[variant] || variantClasses.primary}
        ${sizeClasses[size] || sizeClasses.md}
        ${disabledClasses}
        ${className}
      `}
      {...props}
    >
      {children}
    </button>
  );
};

export default Button;
